mapper.update:
UPDATE cart
SET stock = stock + #{cartDto.stock}
WHERE userInfo_id = #{cartDto.userInfoId}
AND product_id = #{cartDto.productId}

mapper.select:
SELECT count(*)
FROM cart c
WHERE userInfo_id = #{cartDto.userInfoId}
AND product_id = #{cartDto.productId}

mapper.delete:
DELETE
FROM cart
WHERE userInfo_Id = #{userInfoId}
AND id IN

    #{cartId}
